// Normaliza la entrada: busca texto con rutas EXPLÃCITAS
const json = $input.item.json;
let text = null;

// 1. Gemini Audio/Vision: content.parts[0].text
if (json.content && json.content.parts && json.content.parts[0] && json.content.parts[0].text) {
  text = json.content.parts[0].text;
}
// 2. Gemini candidates: candidates[0].content.parts[0].text
if (!text && json.candidates && json.candidates[0] && json.candidates[0].content && json.candidates[0].content.parts && json.candidates[0].content.parts[0]) {
  text = json.candidates[0].content.parts[0].text;
}
// 3. Campo directo 'text'
if (!text && typeof json.text === 'string' && json.text.length > 2) {
  text = json.text;
}
// 4. Campo 'output'
if (!text && typeof json.output === 'string' && json.output.length > 2) {
  text = json.output;
}
// 5. Campo 'content' como string
if (!text && typeof json.content === 'string' && json.content.length > 2) {
  text = json.content;
}
// 6. Campo 'response'
if (!text && typeof json.response === 'string' && json.response.length > 2) {
  text = json.response;
}
// 7. Telegram: message.text
if (!text && json.message && typeof json.message.text === 'string') {
  text = json.message.text;
}
// 8. Ultimo recurso: buscar cualquier string en el JSON completo
if (!text) {
  const raw = JSON.stringify(json);
  const match = raw.match(/"text":\s*"([^"]{3,})"/); 
  if (match) text = match[1];
}

if (text && text.trim().length > 0) {
  return { json: { text: text.trim() } };
}

return { json: { text: '[Audio ininteligible] Debug: ' + JSON.stringify(json).substring(0, 200) } };

// Agrega el contexto en un solo bloque
const context = items.map(i => i.json.pageContent).join('\n---\n') || "No se encontrÃ³ contexto estratÃ©gico.";

// Recuperamos el input original del nodo anterior (Normalizar Entrada)
// Como 'Recuperar Contexto' puede devolver varios items, necesitamos acceder a los datos del padre.
// En n8n, $ode["Normalizar Entrada"].json... nos da el item original.

const input = $('Normalizar Entrada').first().json.text;

return { json: { context, input } };

// Parsea el output del Agente Ejecutivo (Gemini)
// Soporta JSON Ãºnico o ARRAY de tareas mÃºltiples
const json = items[0].json;
let rawText = '';

// 1. Gemini Audio/Vision: content.parts[0].text
if (json.content && json.content.parts && json.content.parts[0] && json.content.parts[0].text) {
  rawText = json.content.parts[0].text;
}
// 2. Gemini candidates
else if (json.candidates && json.candidates[0] && json.candidates[0].content && json.candidates[0].content.parts && json.candidates[0].content.parts[0]) {
  rawText = json.candidates[0].content.parts[0].text;
}
// 3. Campos directos string
else if (typeof json.text === 'string' && json.text.length > 2) {
  rawText = json.text;
}
else if (typeof json.output === 'string' && json.output.length > 2) {
  rawText = json.output;
}
else if (typeof json.response === 'string' && json.response.length > 2) {
  rawText = json.response;
}
// 4. Si content es un objeto, buscar texto dentro
else if (typeof json.content === 'object' && json.content !== null) {
  rawText = JSON.stringify(json.content);
  const innerMatch = rawText.match(/"text":\s*"([\s\S]*?)"/);
  if (innerMatch) rawText = innerMatch[1];
}
// 5. Ultimo recurso
else {
  rawText = JSON.stringify(json);
  const innerMatch = rawText.match(/"text":\s*"([\s\S]*?)"/);
  if (innerMatch) rawText = innerMatch[1];
}

// Limpiar markdown backticks si Gemini los agrega
rawText = rawText.replace(/```json\n?/gi, '').replace(/```\n?/gi, '').trim();

let parsedTasks = [];

try {
  // Intentar parsear como array primero
  const arrayMatch = rawText.match(/\[[\s\S]*\]/);
  if (arrayMatch) {
    parsedTasks = JSON.parse(arrayMatch[0]);
  } else {
    // Intentar como objeto Ãºnico
    const objMatch = rawText.match(/\{[\s\S]*\}/);
    const jsonString = objMatch ? objMatch[0] : rawText;
    parsedTasks = [JSON.parse(jsonString)];
  }
} catch (e) {
  parsedTasks = [{
    title: rawText.length > 3 ? rawText.slice(0, 80) : 'Tarea sin contenido',
    description: 'Error de parseo: ' + e.message + ' | Raw: ' + rawText.substring(0, 150),
    priority: 'medium',
    list_id: 'inbox'
  }];
}

// Asegurar que es un array
if (!Array.isArray(parsedTasks)) parsedTasks = [parsedTasks];

// Mapear a los nombres EXACTOS de Supabase y devolver un item por tarea
return parsedTasks.map(task => ({
  json: {
    title: (task.title && typeof task.title === 'string' && task.title.length > 0) ? task.title : 'Tarea (Parseo Fallido)',
    description: task.description || '',
    priority: task.priority || 'medium',
    list_id: task.listId || task.list_id || 'inbox',
    due_date: task.dueDate || task.due_date || null
  }
}));

